public abstract class Cake{    private String baseFlavour;    private String type;    private int quantity;    public Cake (String baseFlavour, String type)    {        this.baseFlavour = baseFlavour;        this.type = type;    }    public String hasIcing()    {        return "Yum, icing!!";    }    public String hasSprinkles()    {        return "I love sprinkles!";    }    public String isVegan()    {        return "You can't even tell it's vegan....";    }}