import java.util.ArrayList;public class CakeCatalog{    private ArrayList<Cake> cakes;    public CakeCatalog()    {        this.cakes = new ArrayList<>();    }    public int countCakes()    {        return this.cakes.size();    }    public void addCake(Cake cake)    {        this.cakes.add(cake);    }    public void removeCake(Cake cake)    {        this.cakes.remove(cake);    }}