public class Cupcake extends Cake{    private String size;    protected String design;    public Cupcake(String baseFlavour, String type, String size, String design)    {        super(baseFlavour, type);        this.size = size;        this.design = design;    }    @Override    public String hasSprinkles()    {        return "I love sprinkles on cupcakes!";    }    public String addPattern()    {        return "The design is " + this.design;    }}