public class TieredCake extends Cake{    private boolean weddingCake;    private int noOfTiers;    public boolean seperators;    public TieredCake(String baseFlavour, String type, int noOfTiers, boolean weddingCake, boolean seperators)    {        super(baseFlavour, type);        this.noOfTiers = noOfTiers;        this.weddingCake = weddingCake;        this.seperators = seperators;    }    @Override    public String hasIcing()    {        return "All " + this.noOfTiers + " tiers have different coloured icing, how cool!";    }    public String addDifferentFlavours()    {        return "All 3 tiers have different added flavours, how cool!";    }    public String hasSeperators()    {        return "does this cake have separators? " + this.seperators;    }}