import org.junit.jupiter.api.BeforeEach;import org.junit.jupiter.api.Test;import static org.assertj.core.api.AssertionsForClassTypes.assertThat;public class CakeCatalogTest{    CakeCatalog cakeCatalog;    @BeforeEach    void setUp()    {        cakeCatalog = new CakeCatalog();    }    @Test    void canCountCakes()    {        assertThat(cakeCatalog.countCakes()).isEqualTo(0);    }    @Test    void canAddCake()    {        Cake cake = new Cupcake("Red velvet" , "sponge" , "small" , "polka dot");        cakeCatalog.addCake(cake);        assertThat(cakeCatalog.countCakes()).isEqualTo(1);    }    @Test    void canRemoveCake()    {        Cake cake = new TieredCake("Vanilla" , "madeira"  , 4 , false, false);        cakeCatalog.addCake(cake);        cakeCatalog.removeCake(cake);        assertThat(cakeCatalog.countCakes()).isEqualTo(0);    }}