import org.junit.jupiter.api.BeforeEach;import org.junit.jupiter.api.Test;import static org.assertj.core.api.AssertionsForClassTypes.assertThat;public class CakeTest{    Cake cake;    Cake brownie;    Cake cupcake;    @BeforeEach    void setUp()    {        cake = new TieredCake("Vanilla" , "madeira" , 4 , false , false);        brownie = new Traybake("Chocolate" , "brownie" , 8 , "Square" , "chocolate goo");        cupcake = new Cupcake("Red velvet" , "sponge" , "small" , "Christmas themed");    }    @Test    void canHaveSprinkles()    {        assertThat(cake.hasSprinkles()).isEqualTo("I love sprinkles!");    }    @Test    void canHaveIcing()    {        assertThat(brownie.hasIcing()).isEqualTo("Yum, icing!!");    }    @Test    void canBeVegan()    {        assertThat(cupcake.isVegan()).isEqualTo("You can't even tell it's vegan....");    }}