import org.junit.jupiter.api.BeforeEach;import org.junit.jupiter.api.Test;import static org.assertj.core.api.AssertionsForClassTypes.assertThat;public class CupcakeTest{    Cupcake cupcake;    @BeforeEach    void setUp()    {        cupcake = new Cupcake("Blueberry" , "sponge" , "medium" , "floral");    }    @Test    void canHaveIcing()    {        assertThat(cupcake.hasIcing()).isEqualTo("Yum, icing!!");    }    @Test    void canHaveSprinkes()    {        assertThat(cupcake.hasSprinkles()).isEqualTo("I love sprinkles on cupcakes!");    }    @Test    void canBeVegan()    {        assertThat(cupcake.isVegan()).isEqualTo("You can't even tell it's vegan....");    }    @Test    void canAddPattern()    {        assertThat(cupcake.addPattern()).isEqualTo("The design is floral");    }}