import org.junit.jupiter.api.BeforeEach;import org.junit.jupiter.api.Test;import static org.assertj.core.api.AssertionsForClassTypes.assertThat;public class TieredCakeTest{    TieredCake tieredCake;    @BeforeEach    void setUp()    {        tieredCake = new TieredCake("carrot" , "victoria sponge" , 3 , true , true);    }    @Test    void canHaveIcing()    {        assertThat(tieredCake.hasIcing()).isEqualTo("All 3 tiers have different coloured icing, how cool!");    }    @Test    void canHaveSprinkes()    {        assertThat(tieredCake.hasSprinkles()).isEqualTo("I love sprinkles!");    }    @Test    void canBeVegan()    {        assertThat(tieredCake.isVegan()).isEqualTo("You can't even tell it's vegan....");    }    @Test    void hasDifferentFlavours()    {        assertThat(tieredCake.addDifferentFlavours()).isEqualTo("All 3 tiers have different added flavours, how cool!");    }    @Test    void canHaveSeperators()    {        assertThat(tieredCake.hasSeperators()).isEqualTo("does this cake have separators? true");    }}